DROP TABLE MAQUINARIA;

CREATE TABLE MAQUINARIA (
    NUMERO          SMALLINT,
    DESCRIPCION     VARCHAR,
    MARCA           VARCHAR NOT NULL,
    MODELO          VARCHAR(20),
    NSERIE          VARCHAR(25) PRIMARY KEY,
    PERIODO         VARCHAR(8),
    ACTIVOFIJO      VARCHAR(12),
    ZONA            VARCHAR(30),
    PLANTA          VARCHAR(1),
    FECHA_COMPRA    DATE,
    EN_USO          BOOLEAN,
    EN_SERVICIO     BOOLEAN,
    CAUSA           VARCHAR(100)
);


